document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("tools-btn"),e=document.getElementById("drawer"),n=document.getElementById("drawer-close-btn");t.addEventListener("click",function(){e.classList.add("active")}),n.addEventListener("click",function(){e.classList.remove("active")}),window.addEventListener("click",function(t){t.target===e&&e.classList.remove("active")})})