document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("add-task-btn"),e=document.getElementById("task-search"),n=document.getElementById("tasks-container");t.addEventListener("click",function(){const e=prompt("Enter a new task:");e&&s(e)});function s(e){const t=document.createElement("div");t.classList.add("task-item"),t.textContent=e,n.appendChild(t)}e.addEventListener("input",function(){const t=e.value.toLowerCase(),n=document.querySelectorAll(".task-item");n.forEach(e=>{const n=e.textContent.toLowerCase();n.includes(t)?e.style.display="block":e.style.display="none"})})})