document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("add-task-btn"),e=document.getElementById("task-search"),n=document.getElementById("tasks-container");t.addEventListener("click",function(){const e=prompt("Enter a new task:");e&&s(e)});function s(e){const t=document.createElement("div");t.classList.add("task-item");const s=document.createElement("input");s.type="checkbox",s.classList.add("task-checkbox");const o=document.createElement("label");o.classList.add("task-label"),o.textContent=e,t.appendChild(s),t.appendChild(o),n.appendChild(t)}e.addEventListener("input",function(){const t=e.value.toLowerCase(),n=document.querySelectorAll(".task-item");n.forEach(e=>{const n=e.textContent.toLowerCase();n.includes(t)?e.style.display="block":e.style.display="none"})})})